import React, {useState} from 'react'
import Link from 'next/link'

const Pesquisa = () => {
    const [form, setForm] = useState({
        Nome: '',
        Email: '',
        Whatsapp: ''
    })

    const [success, setSuccess] = useState(false)
    const [retorno, setRetorno] = useState({})     

    const save = async () => {

        try{
            const response = await fetch('/api/save', {
                method: 'POST',
                body: JSON.stringify(form)
            })
            const data = await response.json()
            setSuccess(true)
            setRetorno(data)
        }
        catch(err){

        }        
    } 

    const onChance = evt => {
        const value = evt.target.value
        const key = evt.target.name
        setForm(old => ({
            ...old,
            [key]:value
        }))
    }

    return (
        <div className='pt-6'>
            <h1 className='text-center font-bold my-4 text-2xl'>Críticas e sugestões</h1>
            <p className='mt-12 text-center mb-6'>
                Nossa empresa sempre busca por atender melhor seus clientes. <br/>
                Poi isso, estamos sempre abertos a ouvir a sua opnião.
            </p>   
            { !success &&
                <div className='w-1/5 mx-auto'>
                    <label className='font-bold'>Nome</label>
                    <input type='text' 
                    className='p-4 block shadow bg-blue-100 my-2 rounded' 
                    placeholder='Nome' 
                    onChange={onChance} name='Nome' 
                    value={form.Nome} />

                    <label className='font-bold'>E-mail</label>
                    <input type='text' 
                    className='p-4 block shadow bg-blue-100 my-2 rounded' 
                    placeholder='E-mail' 
                    onChange={onChance}
                    name='Email'
                    value={form.Email} />

                    <label className='font-bold'>Whatsapp</label>
                    <input type='text' 
                    className='p-4 block shadow bg-blue-100 my-2 rounded' 
                    placeholder='Whatsapp' onChange={onChance}
                    name='Whatsapp'
                    value={form.Whatsapp} />

                    <button className='bg-blue-400 px-12 py-4 font-bold rounded-lg shadow-lg hover:shadow' onClick={save}>Salvar</button>
                </div>
            }          
            {success &&
                <div className='w-1/5 mx-auto'>
                    <p className='bg-blue-100 border-t border-b border-blue-500 text-blue-700 px-4 py-3'>Obrigado por contribuir com sua sugestão ou crítica</p>
                    Cupom: {JSON.stringify(retorno)}
                </div>
            }
        </div>
    )
}

export default Pesquisa
