import { GoogleSpreadsheet } from 'google-spreadsheet'
import credentials from '../../credentials.json'
import moment from 'moment'

const doc = new GoogleSpreadsheet('1oDFsbvCF2fkkxmjMvC5K2i6Uld6YhA1HI9isZ8ZZLL4')

export default async(req, res) => {
    try {    
        await doc.useServiceAccountAuth(credentials)
        await doc.loadInfo()            
        const sheet = doc.sheetsByIndex[1]
        const data = JSON.parse(req.body)

        const sheetConfig = doc.sheetsByIndex[2]
        await sheetConfig.loadCells('A2:B2')

        const mostraPromocaoCell = sheetConfig.getCell(1, 0)    
        const textoCell = sheetConfig.getCell(1, 1)

        let Cupom = ''
        let Promo = ''
        if(mostraPromocaoCell.value === 'VERDADEIRO'){
            Cupom = parseInt(moment().format('YYMMDDHHmmssSSS')).toString(16).toUpperCase()
            Promo = textoCell.value  
        }

        await sheet.addRow({
            Nome: data.Nome,
            Email: data.Email,
            Whatsapp: data.Whatsapp,
            Datapromo: moment().format('DD/MM/YYYY HH:mm:ss'),
            Nota: 5,
            Cupom,
            Promo
        })
        res.end(req.body)
    } catch (error) {
        console.log(error)   
        res.end('error')
    }
}
